import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import jwt_decode from 'jwt-decode';

const CreateUser = () => {
    let params = useParams();
    const [id, setId] = useState(params.id);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const history = useNavigate();
    const [token, setToken] = useState('');
    const [expired, setExpired] = useState('');
    const axiosJwt = axios.create();

    axiosJwt.interceptors.request.use(async (config) => {
        const currentDate = new Date();
        if (expired * 1000 < currentDate.getTime()) {
            const response = await axios.get('http://localhost:5000/token');
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decode = jwt_decode(response.data.accessToken);
            setName(decode.name);
            setExpired(decode.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    }
    );

    useEffect(() => {
        cekId();
    // eslint-disable-next-line react-hooks/exhaustive-deps
}, []);

    const cekId = async () => {
        if (id === '_add') {
            return
        } else {
            const res = await axios.get('http://localhost:5000/users/' + id, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            let User = res.data;
            setName(User.name);
            setEmail(User.email);
            setPassword(User.password);
        }

    }

    const saveOrUpdateUser = async (e) => {
        e.preventDefault();
        let User = {
            name: name,
            email: email,
            password: password,
        }
        console.log('User => ' + JSON.stringify(User));

        if (id === '_add') {
            await axios.post('http://localhost:5000/adduser',
                {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                }, User)
                .then(res => {
                    history('/home');
                });
        } else {
            await axios.put('http://localhost:5000/users/' + id,
                {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                }, User)
                .then(res => {
                    history('/home');
                });
        }
    }

    function cancel() {
        history('/home');
    }

    function getTitle() {
        if (id === "_add") {
            return <h3 className="text-center">Add User</h3>
        } else {
            return <h3 className="text-center">Update User</h3>
        }
    }

    return (
        <div>
            <br></br>
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        {getTitle()}
                    </div>
                    <div className="card-body">
                        <form>
                            <div className="form-group">
                                <label>Name </label>
                                <input placeholder="Name" name="name" className="form-control"
                                    value={name} onChange={(e) => setName(e.target.value)} />
                            </div>
                            <div className="form-group">
                                <label>Email</label>
                                <input placeholder="Email" name="email" className="form-control"
                                    value={email} onChange={(e) => setEmail(e.target.value)} />
                            </div>
                            <div className="form-group">
                                <label>Password</label>
                                <input placeholder="*****" name="alamat" type='password' className="form-control"
                                    value={password} onChange={(e) => setPassword(e.target.value)} />
                            </div>
                            <br></br>
                            <button className="btn btn-success" onClick={saveOrUpdateUser} >Simpan</button>
                            <button className="btn btn-danger" onClick={cancel}>Batal</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CreateUser
