import React, { useEffect, useState } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [name, setName] = useState('');
  const [token, setToken] = useState('');
  const [expired, setExpired] = useState('');
  const [users, setUsers] = useState([]);
  const history = useNavigate();

  const axiosJwt = axios.create();

  useEffect(() => {
    refreshToken();
    getUsers();
        // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const refreshToken = async () => {
    try {
      const response = await axios.get('http://localhost:5000/token');
      setToken(response.data.accessToken);
      const decode = jwt_decode(response.data.accessToken);
      setName(decode.name);
      setExpired(decode.exp);
    } catch (error) {
      if (error.response) {
        history('/');
      }
    }
  }

  axiosJwt.interceptors.request.use(async (config) => {
    const currentDate = new Date();
    if (expired * 1000 < currentDate.getTime()) {
      const response = await axios.get('http://localhost:5000/token');
      config.headers.Authorization = `Bearer ${response.data.accessToken}`;
      setToken(response.data.accessToken);
      const decode = jwt_decode(response.data.accessToken);
      setName(decode.name);
      setExpired(decode.exp);
    }
    return config;
  }, (error) => {
    return Promise.reject(error);
  }
  );

  const getUsers = async () => {
    const response = await axiosJwt.get('http://localhost:5000/users', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    setUsers(response.data);
  }

  function addUser () {
    alert('Tambah User')
    // history('');
  }

  function editUser  (id)  {
    alert('Edit User')
    // history('');
  }

  function viewUser  (id)  {
    alert('View User')
    // history('');
  }

  const deleteUser =async(id)=> {
    alert(id);
    // const confirmBox = window.confirm(
    //   "Do you really want to delete this Crumb?"
    // )
    // if (confirmBox === true) {
    //   handleDeleteCrumb(bookmark)
    // }
    var proceed = window.confirm('Apakah anda yakin hapus?');
    if (proceed) {
      const response = await axiosJwt.get('http://localhost:5000/users/'+id, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      alert(response.data.msg);
    } else {
      return
    }
  }

  return (
    <div className='container mt-5'>
      <h1>Selamat datang {name} di Bootcamp</h1>
      <hr></hr>
      <button onClick={addUser} className='button is-info'>Add User</button>
      <table className='table is-striped is-fullwidth'>
        <thead>
          <tr>
            <th>No</th>
            <th>Name</th>
            <th>Email</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={user.id}>
              <td>{index + 1}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>
                <button onClick={() => editUser(user.id)}
                  className='button is-default'>Edit</button>
                <button style={{ marginleft: "10px" }}
                  onClick={() => deleteUser(user.id)}
                  className='button is-danger'>Delete</button>
                <button style={{ marginleft: "10px" }}
                  onClick={() => viewUser(user.id)}
                  className='button is-success'>View</button>

              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}

export default Home
